VOICE_ENABLED = SR_AVAILABLE and TTS_AVAILABLE

# ------------------ Utilities ------------------
def safe_print(*args, **kwargs):
    try:
        print(*args, **kwargs)
    except:
        pass

def speak(text):
    """Text to speech (if available)"""
    if not TTS_AVAILABLE:
        return
    try:
        engine = speak.engine
    except AttributeError:
        speak.engine = pyttsx3.init()
        engine = speak.engine
    try:
        engine.say(text)
        engine.runAndWait()
    except Exception as e:
        safe_print("TTS error:", e)

def listen(timeout=4, phrase_time_limit=6):
    """Listen once from microphone and return recognized Hindi text (if SR available)"""
    if not SR_AVAILABLE:
        return ""
    r = sr.Recognizer()
    try:
        with sr.Microphone() as source:
            r.adjust_for_ambient_noise(source, duration=0.6)
            safe_print("üé§ Listening...")
            audio = r.listen(source, timeout=timeout, phrase_time_limit=phrase_time_limit)
    except Exception as e:
        safe_print("Mic/timeout error:", e)
        return ""
    try:
        text = r.recognize_google(audio, language='hi-IN')
        safe_print("Heard:", text)
        return text
    except Exception as e:
        safe_print("Recognition error:", e)
        return ""

def normalize(text):
    if not text:
        return ""
    text = text.lower().strip()
    # remove punctuation except + - * / for math detection
    text = re.sub(r'[^\w\s\+\-\*\/]', '', text)
    return text

# ------------------ Memory ------------------
def load_memory():
    if os.path.exists(MEMORY_FILE):
        try:
            with open(MEMORY_FILE, "r", encoding="utf-8") as f:
                return json.load(f)
        except:
            return {}
    return {}

def save_memory():
    try:
        with open(MEMORY_FILE, "w", encoding="utf-8") as f:
            json.dump(memory, f, ensure_ascii=False, indent=2)
    except Exception as e:
        safe_print("Save memory error:", e)

memory = load_memory()
