# ------------------ Helper functions for chatbot ------------------
def find_answer_for_query(query):
    """Return answer for a given user query (string)."""
    qnorm = normalize(query)
    if not qnorm:
        return "‡§Æ‡•à‡§Ç ‡§∏‡•Å‡§® ‡§®‡§π‡•Ä‡§Ç ‡§™‡§æ‡§Ø‡§æ ‚Äî ‡§ï‡•É‡§™‡§Ø‡§æ ‡§´‡§ø‡§∞ ‡§¨‡•ã‡§≤‡§ø‡§è‡•§"

    # 1) check direct matches (exact or contains)
    # check exact
    for q,a in QA_LIST:
        if q.lower() == query.lower().strip():
            return a if a not in ("__JOKE__", "__SONG__") else ("__JOKE__" if a=="__JOKE__" else "__SONG__")

    # 2) contains match (best-effort)
    for q,a in QA_LIST:
        if normalize(q) in qnorm:
            return a if a not in ("__JOKE__", "__SONG__") else ("__JOKE__" if a=="__JOKE__" else "__SONG__")

    # 3) math evaluation attempt: detect patterns like '5 + 3', '‡§¶‡•ã + ‡§§‡•Ä‡§®' etc
    m = re.search(r'([0-9]+)\s*([\+\-\*\/])\s*([0-9]+)', query)
    if m:
        a = int(m.group(1)); op = m.group(2); b = int(m.group(3))
        try:
            if op == '+': return str(a+b)
            if op == '-': return str(a-b)
            if op == '*': return str(a*b)
            if op == '/':
                if b==0: return "Division by zero!"
                val = a/b
                if abs(val - round(val)) < 1e-9:
                    return str(int(val))
                else:
                    return str(round(val,2))
        except Exception:
            pass

    # 4) keyword-based fallback
    if "‡§Æ‡§ú‡§æ‡§ï" in qnorm or "‡§ú‡•ã‡§ï" in qnorm:
        return "__JOKE__"
    if "‡§ó‡§æ‡§®‡§æ" in qnorm:
        return "__SONG__"

    # 5) small talk / memory
    if "‡§®‡§æ‡§Æ" in qnorm:
        if "‡§Æ‡•á‡§∞‡§æ ‡§®‡§æ‡§Æ" in qnorm:
            # user telling name like "‡§Æ‡•á‡§∞‡§æ ‡§®‡§æ‡§Æ ‡§¶‡•á‡§µ ‡§π‡•à"
            m = re.search(r'‡§Æ‡•á‡§∞‡§æ ‡§®‡§æ‡§Æ\s+([^\s]+)', query, re.I)
            if m:
                name = m.group(1)
                memory["name"] = name
                save_memory()
                return f"‡§Ö‡§ö‡•ç‡§õ‡§æ {name}, ‡§Ü‡§™‡§∏‡•á ‡§Æ‡§ø‡§≤‡§ï‡§∞ ‡§ñ‡•Å‡§∂‡•Ä ‡§π‡•Å‡§à!"
        if memory.get("name"):
            return f"‡§Ü‡§™‡§ï‡§æ ‡§®‡§æ‡§Æ {memory['name']} ‡§π‡•à‡•§"
        return "‡§Æ‡•á‡§∞‡§æ ‡§®‡§æ‡§Æ RaazGPT ‡§π‡•à ‚Äî ‡§Ü‡§™‡§ï‡§æ ‡§π‡§ø‡§Ç‡§¶‡•Ä ‡§¨‡•â‡§ü!"

    # 6) fallback
    return "‡§Æ‡§æ‡§´‡§º ‡§ï‡•Ä‡§ú‡§ø‡§è, ‡§Æ‡•Å‡§ù‡•á ‡§á‡§∏‡§ï‡§æ ‡§â‡§§‡•ç‡§§‡§∞ ‡§®‡§π‡•Ä‡§Ç ‡§Æ‡§ø‡§≤‡§æ ‚Äî ‡§ï‡•ç‡§Ø‡§æ ‡§Ü‡§™ ‡§Ö‡§≤‡§ó ‡§∂‡§¨‡•ç‡§¶‡•ã‡§Ç ‡§Æ‡•á‡§Ç ‡§™‡•Ç‡§õ‡•á‡§Ç‡§ó‡•á?"

# ------------------ GUI Chat functions ------------------
def add_chat_line(text):
    chatbox.config(state=tk.NORMAL)
    chatbox.insert(tk.END, text + "\n")
    chatbox.config(state=tk.DISABLED)
    chatbox.see(tk.END)

def handle_user_text(u):
    add_chat_line("‡§Ü‡§™: " + u)
    # process in a thread to keep UI responsive for TTS/listen
    threading.Thread(target=process_and_respond, args=(u,)).start()

def process_and_respond(user_text):
    ans = find_answer_for_query(user_text)
    if ans == "__JOKE__":
        j = random.choice(jokes)
        add_chat_line("ü§ñ: " + j)
        speak(j)
        return
    if ans == "__SONG__":
        s = random.choice(songs)
        add_chat_line("ü§ñ: " + s)
        speak(s)
        return
    # normal answer
    add_chat_line("ü§ñ: " + str(ans))
    speak(str(ans))

def on_send_click():
    txt = entry.get().strip()
    if not txt:
        return
    entry.delete(0, tk.END)
    handle_user_text(txt)

def on_voice_click():
    if not SR_AVAILABLE:
        messagebox.showinfo("Voice not available", "SpeechRecognition library not installed or microphone not available.")
        return
    add_chat_line("üé§: (Listening...)")
    # listen in separate thread
    def listen_thread():
        q = listen(timeout=5, phrase_time_limit=6)
        if not q:
            add_chat_line("ü§ñ: ‡§Æ‡•à‡§Ç ‡§∏‡•Å‡§® ‡§®‡§π‡•Ä‡§Ç ‡§™‡§æ‡§Ø‡§æ‡•§")
            return
        handle_user_text(q)
    threading.Thread(target=listen_thread).start()

def on_quit():
    save_memory()
    root.destroy()

