def speak(text):
    """Text to speech (if available)"""
    if not TTS_AVAILABLE:
        return
    try:
        engine = speak.engine
    except AttributeError:
        speak.engine = pyttsx3.init()
        engine = speak.engine
    try:
        engine.say(text)
        engine.runAndWait()
    except Exception as e:
        safe_print("TTS error:", e)

def listen(timeout=4, phrase_time_limit=6):
    """Listen once from microphone and return recognized Hindi text (if SR available)"""
    if not SR_AVAILABLE:
        return ""
    r = sr.Recognizer()
    try:
        with sr.Microphone() as source:
            r.adjust_for_ambient_noise(source, duration=0.6)
            safe_print("ЁЯОд Listening...")
            audio = r.listen(source, timeout=timeout, phrase_time_limit=phrase_time_limit)
    except Exception as e:
        safe_print("Mic/timeout error:", e)
        return ""
    try:
        text = r.recognize_google(audio, language='hi-IN')
        safe_print("Heard:", text)
        return text
    except Exception as e:
        safe_print("Recognition error:", e)
        return ""

def normalize(text):
    if not text:
        return ""
    text = text.lower().strip()
    # remove punctuation except + - * / for math detection
    text = re.sub(r'[^\w\s\+\-\*\/]', '', text)
    return text

