# ------------------ Generate large QA dataset programmatically ------------------
# We'll create variants from curated_facts and also autogen many math questions and rephrased variants.

def generate_variants(fact_q):
    """Produce small variants for a fact question to increase dataset size."""
    q = fact_q.strip('?').strip()
    variants = []
    variants.append(q)
    variants.append(q + " рдХреНрдпрд╛ рд╣реИ")
    variants.append("рдмрддрд╛рдЗрдП: " + q)
    variants.append(q + " рдмрддрд╛рдЗрдП")
    variants.append("рдХрд╣рд┐рдпреЗ, " + q + "?")
    # if short, add "рдХреМрди рд╕рд╛/рдХреМрди" variants
    if len(q.split()) <= 6:
        variants.append("рдХреМрди рд╕рд╛ " + q)
        variants.append("рдХреМрди рд╣реИ " + q)
    return list(dict.fromkeys([v.strip() for v in variants]))  # de-dup

def gen_math_questions(count=1800):
    """Generate many math arithmetic questions (simple)"""
    mq = []
    ops = ['+', '-', '*', '/']
    for i in range(count):
        a = random.randint(1, 999)
        b = random.randint(1, 999)
        op = random.choice(ops)
        if op == '/':
            b = random.randint(1, 20)
            a = b * random.randint(1, 20)  # make integer often
        q = f"{a} {op} {b}"
        # ensure readable variants
        q_variant = q + " = ?"
        ans = None
        try:
            if op == '/':
                val = a / b
                if abs(val - round(val)) < 1e-9:
                    ans = str(int(val))
                else:
                    ans = str(round(val, 2))
            else:
                ans = str(int(eval(q)))
        except:
            continue
        mq.append((q_variant, ans))
    return mq

# Build big QA set
def build_large_qa():
    qa = []

    # 1) expand curated_facts into many variants
    for q,a in curated_facts:
        variants = generate_variants(q)
        for v in variants:
            qa.append((v, a))

    # 2) add some direct static GK Qs repeated with phrasing
    more_safe = [
        ("рднрд╛рд░рдд рдХрд╛ рд░рд╛рд╖реНрдЯреНрд░реАрдп рдлреВрд▓ рдХреМрди рд╕рд╛ рд╣реИ", "рдХрдорд▓"),
        ("рднрд╛рд░рдд рдХрд╛ рд░рд╛рд╖реНрдЯреНрд░реАрдп рдЦреЗрд▓ рдХреМрди рд╕рд╛ рд╣реИ", "рд╣реЙрдХреА"),
        ("рднрд╛рд░рдд рдХрд╛_currency рдХреНрдпрд╛ рд╣реИ", "рднрд╛рд░рддреАрдп рд░реБрдкрдпрд╛"),
        ("рддрд╛рдЬрдорд╣рд▓ рдХрд┐рд╕рдиреЗ рдмрдирд╡рд╛рдпрд╛", "рд╢рд╛рд╣рдЬрд╣рд╛рдВ"),
        ("рдЧреБрд░реБрддреНрд╡рд╛рдХрд░реНрд╖рдг рдХреЗ рдкрд┐рддрд╛ рдХреМрди рд╣реИрдВ", "рдиреНрдпреВрдЯрди"),
        ("рд╣рд┐рдиреНрджреА рднрд╛рд░рдд рдореЗрдВ рдХрд┐рд╕реЗ рдХрд╣рд╛ рдЬрд╛рддрд╛ рд╣реИ", "рд░рд╛рд╖реНрдЯреНрд░реАрдп рднрд╛рд╖рд╛ (рдПрдХ рдореМрдХрд╛рд┐рдХ рднрд╛рд╖рд╛)"),
    ]
    for q,a in more_safe:
        qa.append((q, a))
        qa.extend([(q + " рдХреНрдпрд╛ рд╣реИ", a), ("рдмрддрд╛рдЗрдП: " + q, a)])

    # 3) add math generated
    math_gen = gen_math_questions(1800)  # many
    qa.extend(math_gen)

    # 4) add jokes & songs triggers (map to response rather than Q->A)
    # We'll add trigger questions that request jokes or songs
    joke_triggers = ["рдордЬрд╛рдХ", "рдордЬрд╛рдХ рд╕реБрдирд╛рдУ", "рдХреЛрдИ рдЬреЛрдХ рдмрддрд╛рдУ", "рдордЬрд╝рд╛рдХ рдХрд░"]
    for t in joke_triggers:
        qa.append((t, "__JOKE__"))

    song_triggers = ["рдЧрд╛рдирд╛", "рдЧрд╛рдирд╛ рд╕реБрдирд╛рдУ", "рдХреЛрдИ рдЧрд╛рдирд╛", "рдЧрд╛рдирд╛ рдЧрд╛рдУ"]
    for t in song_triggers:
        qa.append((t, "__SONG__"))

    # 5) Add many rephrased variants of math/facts to reach target
    # keep adding rephrasings of random curated facts to expand
    pool_fact_qs = [q for q,a in curated_facts]
    while len(qa) < TARGET_QA_COUNT:
        base = random.choice(pool_fact_qs)
        vars_ = generate_variants(base)
        var = random.choice(vars_)
        qa.append((var + " рдХреНрдпрд╛ рд╣реИ", next(a for q,a in curated_facts if q==base)))
    # dedupe preserving order
    seen = set()
    uniq = []
    for q,a in qa:
        key = (q.strip().lower(), str(a).strip().lower())
        if key not in seen:
            seen.add(key)
            uniq.append((q.strip(), a))
    return uniq

