# ------------------ Helper functions for chatbot ------------------
def find_answer_for_query(query):
    """Return answer for a given user query (string)."""
    qnorm = normalize(query)
    if not qnorm:
        return "рдореИрдВ рд╕реБрди рдирд╣реАрдВ рдкрд╛рдпрд╛ тАФ рдХреГрдкрдпрд╛ рдлрд┐рд░ рдмреЛрд▓рд┐рдПред"

    # 1) check direct matches (exact or contains)
    # check exact
    for q,a in QA_LIST:
        if q.lower() == query.lower().strip():
            return a if a not in ("__JOKE__", "__SONG__") else ("__JOKE__" if a=="__JOKE__" else "__SONG__")

    # 2) contains match (best-effort)
    for q,a in QA_LIST:
        if normalize(q) in qnorm:
            return a if a not in ("__JOKE__", "__SONG__") else ("__JOKE__" if a=="__JOKE__" else "__SONG__")

    # 3) math evaluation attempt: detect patterns like '5 + 3', 'рджреЛ + рддреАрди' etc
    m = re.search(r'([0-9]+)\s*([\+\-\*\/])\s*([0-9]+)', query)
    if m:
        a = int(m.group(1)); op = m.group(2); b = int(m.group(3))
        try:
            if op == '+': return str(a+b)
            if op == '-': return str(a-b)
            if op == '*': return str(a*b)
            if op == '/':
                if b==0: return "Division by zero!"
                val = a/b
                if abs(val - round(val)) < 1e-9:
                    return str(int(val))
                else:
                    return str(round(val,2))
        except Exception:
            pass

    # 4) keyword-based fallback
    if "рдордЬрд╛рдХ" in qnorm or "рдЬреЛрдХ" in qnorm:
        return "__JOKE__"
    if "рдЧрд╛рдирд╛" in qnorm:
        return "__SONG__"

    # 5) small talk / memory
    if "рдирд╛рдо" in qnorm:
        if "рдореЗрд░рд╛ рдирд╛рдо" in qnorm:
            # user telling name like "рдореЗрд░рд╛ рдирд╛рдо рджреЗрд╡ рд╣реИ"
            m = re.search(r'рдореЗрд░рд╛ рдирд╛рдо\s+([^\s]+)', query, re.I)
            if m:
                name = m.group(1)
                memory["name"] = name
                save_memory()
                return f"рдЕрдЪреНрдЫрд╛ {name}, рдЖрдкрд╕реЗ рдорд┐рд▓рдХрд░ рдЦреБрд╢реА рд╣реБрдИ!"
        if memory.get("name"):
            return f"рдЖрдкрдХрд╛ рдирд╛рдо {memory['name']} рд╣реИред"
        return "рдореЗрд░рд╛ рдирд╛рдо RaazGPT рд╣реИ тАФ рдЖрдкрдХрд╛ рд╣рд┐рдВрджреА рдмреЙрдЯ!"

    # 6) fallback
    return "рдорд╛рдлрд╝ рдХреАрдЬрд┐рдП, рдореБрдЭреЗ рдЗрд╕рдХрд╛ рдЙрддреНрддрд░ рдирд╣реАрдВ рдорд┐рд▓рд╛ тАФ рдХреНрдпрд╛ рдЖрдк рдЕрд▓рдЧ рд╢рдмреНрджреЛрдВ рдореЗрдВ рдкреВрдЫреЗрдВрдЧреЗ?"

# ------------------ GUI Chat functions ------------------
def add_chat_line(text):
    chatbox.config(state=tk.NORMAL)
    chatbox.insert(tk.END, text + "\n")
    chatbox.config(state=tk.DISABLED)
    chatbox.see(tk.END)

def handle_user_text(u):
    add_chat_line("рдЖрдк: " + u)
    # process in a thread to keep UI responsive for TTS/listen
    threading.Thread(target=process_and_respond, args=(u,)).start()

def process_and_respond(user_text):
    ans = find_answer_for_query(user_text)
    if ans == "__JOKE__":
        j = random.choice(jokes)
        add_chat_line("ЁЯдЦ: " + j)
        speak(j)
        return
    if ans == "__SONG__":
        s = random.choice(songs)
        add_chat_line("ЁЯдЦ: " + s)
        speak(s)
        return
    # normal answer
    add_chat_line("ЁЯдЦ: " + str(ans))
    speak(str(ans))

def on_send_click():
    txt = entry.get().strip()
    if not txt:
        return
    entry.delete(0, tk.END)
    handle_user_text(txt)

def on_voice_click():
    if not SR_AVAILABLE:
        messagebox.showinfo("Voice not available", "SpeechRecognition library not installed or microphone not available.")
        return
    add_chat_line("ЁЯОд: (Listening...)")
    # listen in separate thread
    def listen_thread():
        q = listen(timeout=5, phrase_time_limit=6)
        if not q:
            add_chat_line("ЁЯдЦ: рдореИрдВ рд╕реБрди рдирд╣реАрдВ рдкрд╛рдпрд╛ред")
            return
        handle_user_text(q)
    threading.Thread(target=listen_thread).start()

def on_quit():
    save_memory()
    root.destroy()

